package collections;

public class implicit_explicit {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//VD explicit
		//tạo ra biến weight với kiểu dữ liệu double
		//tạo ra biến result với kiểu dữ liệu int
		double weight = 20;
		int result = (int) weight;
		System.out.println("giá trị biến weight : " + weight);
		System.out.println("giá trị biến result : " + result);
	}

}
